import * as C from './constants'

class Movement {
    constructor({width, length}){
        this.width = width
        this.length = length

    }

    isInCanvas({x, y}) {
        const { width, length } = this;
        return (
            x > C.PLAY_AREA_LEFT_BOUNDARY
            && x < C.PLAY_AREA_RIGHT_BOUNDARY - width
            && y > C.PLAY_AREA_UP_BOUNDARY
