import * as C from './constants'
import Movement from './movement'

class WeaponMovement extends Movement {
    constructor(attributes) {
        super(attributes)
    }

    getNewPosUsingDir(type, moveData) {
        const { oldX, oldY } = this.getCoordInfo(type, moveData)
        let newX = oldX
        let newY = oldY
        const delta = C.WEAPON_DIST

        switch (moveData.heroDir) {
            case C.DIR_UP:
            case C.DIR_LEFT_UP:
            case C.DIR_RIGHT_UP:
                newY = newY - delta
                break;
            case C.DIR_DOWN:
            case C.DIR_DOWN_LEFT:
            case C.DIR_DOWN_RIGHT:
                newY = newY + delta
                break;
            case C.DIR_LEFT:
                newX = newX - delta
                break;
            case C.DIR_RIGHT:
                newX = newX + delta
                break;
            case C.DIR_DOWN_LEFT:
                newX = newX - delta
                newY = newY + delta
                break;
            case C.DIR_DOWN_RIGHT:
                newX = newX + delta
                newY = newY + delta
                break;
        }
        return ({ newX, newY })
    }

    getCoordInfo(type, { x, y, length, width }) {
        let oldX, oldY;
        switch (type) {
            case C.DIR_LEFT:
            case C.DIR_RIGHT:
            case C.DIR_RIGHT_UP:
            case C.DIR_DOWN_RIGHT:
            case C.DIR_DOWN_LEFT:
            case C.DIR_LEFT_UP:
                oldX = x + (C.HERO_SPRITE_SCALED_WIDTH - width) / 2
                oldY = y + (C.HERO_SPRITE_SCALED_LENGTH - length) / 2
                break;
            case C.DIR_UP:
            case C.DIR_DOWN:
                oldX = x - (length - C.HERO_SPRITE_SCALED_WIDTH) / 2
                oldY = y - (width - C.HERO_SPRITE_SCALED_LENGTH) / 2
                break;
        }

        return { oldX, oldY }
    }

}

export default WeaponMovement

